import React, { useEffect, useState } from "react";
import HomePageView from "./HomePageView";
import httpService from "../../services/httpService"; // Servicio centralizado para peticiones
import { API_ENDPOINTS } from "../../services/config";

const HomePageContainer = () => {
  const [merchants, setMerchants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar los comerciantes al montar el componente
  useEffect(() => {
    const fetchMerchants = async () => {
      try {
        const data = await httpService(API_ENDPOINTS.MERCHANTS); // Endpoint para obtener los comerciantes
        setMerchants(data.content);
      } catch (err) {
        setError("Error al cargar los comerciantes.");
      } finally {
        setLoading(false);
      }
    };

    fetchMerchants();
  }, []);

  const handleLogout = () => {
    // Eliminar token y redirigir a login
    localStorage.removeItem("jwtToken");
    window.location.href = "/login";
  };

  return (
    <HomePageView
      merchants={merchants}
      loading={loading}
      error={error}
      onLogout={handleLogout}
    />
  );
};

export default HomePageContainer;
