import httpService, { setToken } from "./httpService";
import { API_ENDPOINTS } from "./config";

export const login = async (email, password) => {
  // Hashea la contrase침a y el email para no enviar el texto plano
  const hashedEmail = btoa(email);
  const hashedPassword = btoa(password);
  // Se desencriptara en el backend


  const response = await httpService(API_ENDPOINTS.LOGIN, "POST", {
    email: hashedEmail,
    password: hashedPassword,
  });

  setToken(response.token); // Guarda el token JWT para futuras solicitudes
  return response.token; // Devuelve el token
};

// Cierra sesi칩n y elimina el token JWT
export const logout = () => {
  setToken(null); // Borra el token JWT
  localStorage.removeItem("jwtToken"); // Opcional: Elimina el token del almacenamiento local
};
